module adder_bcd (
    input [3:0] A,
    input [3:0] B,
    input Cin,
    output reg [3:0] Sum,
    output reg Cout
);

always @(*) begin
    integer temp_sum;
    temp_sum = A + B + Cin;
    if (temp_sum > 9) begin
        Sum = (temp_sum + 6) % 10;
        Cout = 1;
    end else begin
        Sum = temp_sum;
        Cout = 0;
    end
end

endmodule

module adder_bcd (
    input [3:0] A,
    input [3:0] B,
    input Cin,
    output reg [3:0] Sum,
    output reg Cout
);

always @(*) begin
    reg [4:0] sum_bin;
    sum_bin = A + B + Cin;
    if (sum_bin > 9) begin
        Sum = sum_bin + 6;
        if (Sum > 15)
            Sum = Sum - 6;
        Cout = 1'b1;
    end else begin
        Sum = sum_bin;
        Cout = 1'b0;
    end
end

endmodule

